import React, { useEffect, useRef } from 'react';

const BackgroundVideo = () => {
  const videoRef = useRef(null);
  const videoSources = [
    'squat.mp4',
    'female.mp4',
    'pushups.mp4',
    // Add more video sources as needed
  ];

  useEffect(() => {
    let currentVideoIndex = 0;

    const playNextVideo = () => {
      const nextVideoIndex = (currentVideoIndex + 1) % videoSources.length;
      const nextVideoSource = videoSources[nextVideoIndex];

      videoRef.current.src = nextVideoSource;
      videoRef.current.play();

      currentVideoIndex = nextVideoIndex;
    };

    videoRef.current.addEventListener('ended', playNextVideo);

    return () => {
      videoRef.current.removeEventListener('ended', playNextVideo);
    };
  }, [videoSources]);

  return (
    <div className="background-video">
      <video ref={videoRef} autoPlay muted loop>
        <source src={videoSources[0]} type="video/mp4" />
        Your browser does not support the video tag.
      </video>
    </div>
  );
};

export default BackgroundVideo;
