import React, { useEffect, useState } from 'react';

const Exercises = () => {
  const [exercises, setExercises] = useState([]);
  const [selectedBodyPart, setSelectedBodyPart] = useState('All');

  useEffect(() => {
    let url = 'http://localhost:9292/exercises';

    if (selectedBodyPart !== 'All') {
      url += `?muscle_group=${selectedBodyPart}`;
    }

    // Fetch exercises from the backend
    fetch(url)
      .then((response) => response.json())
      .then((data) => setExercises(data))
      .catch((error) => console.log(error));
  }, [selectedBodyPart]);

  const handleBodyPartChange = (e) => {
    setSelectedBodyPart(e.target.value);
  };

  return (
    <div>
      <h2>Exercises</h2>
      <div>
        <label>Select Body Part:</label>
        <select value={selectedBodyPart} onChange={handleBodyPartChange}>
          <option value="All">All</option>
          <option value="Chest">Chest</option>
          <option value="Legs">Legs</option>
          <option value="Abs">Abs</option>
          {/* Add more body part options as needed */}
        </select>
      </div>
      <ul>
        {exercises.map((exercise) => (
          <li key={exercise.id}>
            <h3>{exercise.name}</h3>
            <p>{exercise.description}</p>
            <p>Muscle Group: {exercise.muscle_Group}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Exercises;
